import Vue from 'vue'
import VueRouter from 'vue-router'
import Layout from '@/views/layout/Index.vue'
import Login from '@/views/login/Index.vue'
import Home from '@/views/home/Home.vue'

const Product = ()=>import('@/views/product/Index.vue')

Vue.use(VueRouter)

const routes = [
    {
        path: '/',
        component: Layout,
        children:[
            {
                path: '/',
                name: 'home',
                component: Home,
            },
            {
                path: '/product',
                name: 'product',
                component:Product,
                children:[
                    {
                        path: 'list',
                        name: 'product',
                        component:Product,
                    }
                ]
            }
        ]
    },
    {
        path: '/login',
        name: 'login',
        component: Login,
    }
]

const router = new VueRouter({
    // mode: 'history',
    routes
})

export default router
